// import React, {useState} from 'react'
import Main from './Main.js'
import Header from './Header.js'
// import Footer from './Footer.js'

function App() {
	let width = 50;
	let height = 50;
//	const table=document.getElementById("table")	
	function setFocus(e) {
                const focus = document.getElementById('cell-name');              
                focus.value = e.target.id;
        }
	function setBold() {
		const focus = document.getElementById('cell-name');
		const cell = document.getElementById(focus.value);
		if(cell.style.fontWeight==='bold'){
			cell.style.fontWeight='normal';
		} else {
			cell.style.fontWeight='bold';
		}

	}
	function setItalic() {
		const focus = document.getElementById('cell-name');
		const cell = document.getElementById(focus.value);
		if(cell.style.fontStyle==='italic'){
			cell.style.fontStyle==='normal';
		} else {
			cell.style.fontStyle='italic';
		}
	}


	return(
		<>
			<head>
				<link rel="preconnect" href="https://fonts.googleapis.com"></link>
				<link rel="preconnect" href="https://fonts.gstatic.com" crossorigin></link>
				<link href="https://fonts.googleapis.com/css2?family=Open+Sans:ital,wght@0,300..800;1,300..800&display=swap" rel="stylesheet"></link>

			</head>
			<Header setBold={setBold} setItalic={setItalic}/>
			<Main height={height} width={width} onClick={setFocus}/>
		</>
	)
}
export default App;
