import Header from "./Header.js"
import Footer from "./Footer.js"
import TextBlock from "./Text-block.js"
import React, {useState} from 'react'


function App() {
	const [lang, setLang] = useState("RU");
	const setEN = ()=>{
		const EN = document.getElementById("EN");
		const RU = document.getElementById("RU");
		setLang("EN");
		EN.style.backgroundColor="var(--color-selected)";
		RU.style.backgroundColor="";
	}
	const setRU = ()=>{
		const EN = document.getElementById("EN");
		const RU = document.getElementById("RU");
		setLang("RU");
		RU.style.backgroundColor="var(--color-selected)";
		EN.style.backgroundColor="";
	}
  return (
   	<>
		<Header lang={lang} setEN={setEN} setRU={setRU}/>
		<TextBlock top="1000px" text={lang==="RU" ? 'хахаххахахахахахааххахаахаххахахаахаххаахахаааааааааааааааааааааааахахахахахахахахахаххахахахахааххахааххахах' : 'hahahhahhhaahaahahahhahahahahahaaaaaaaaaaahahahahahahahhahahhahahahahahahahhahahahahahahahahhahahahhahahahaha'} />
	  	<Footer lang={lang}/>
	</>
  );
}

export default App;
