import Header from "./Header.js"
import Footer from "./Footer.js"
import React, {useState} from 'react'


function App() {
	const [lang, setLang] = setState;
	const onChange = (lang)=>{
		setLang(lang);
	}
  return (
   	<>
		<Header lang={lang} onChange={this.onChange}/>
	  	<Footer lang={lang}/>
	</>
  );
}

export default App;
