import Header from "./Header.js"
import Footer from "./Footer.js"
import TextBlock from "./Text-block.js"
import React, {useState} from 'react'


function App() {
	const [lang, setLang] = useState("RU");
	const setEN = ()=>{
		const EN = document.getElementById("EN");
		const RU = document.getElementById("RU");
		setLang("EN");
		EN.style.backgroundColor="var(--color-selected)";
		RU.style.backgroundColor="";
	}
	const setRU = ()=>{
		const EN = document.getElementById("EN");
		const RU = document.getElementById("RU");
		setLang("RU");
		RU.style.backgroundColor="var(--color-selected)";
		EN.style.backgroundColor="";
	}
  return (
	  <>
	  <head>
	  <link rel="preconnect" href="https://fonts.googleapis.com"></link>
<link rel="preconnect" href="https://fonts.gstatic.com" crossorigin></link>
<link href="https://fonts.googleapis.com/css2?family=Roboto+Flex:opsz,wght@8..144,100..1000&display=swap" rel="stylesheet"></link>
	  </head>
   		<body>
			<Header lang={lang} setEN={setEN} setRU={setRU}/>
	  		<TextBlock top='900px' height='' head='О нас' text='Мы молодая компания с большим будущим. Мы не ждём чуда, мы его создаём. Автоматизация сельского хозяйства это огромный шаг к улучшению уровня жизни в стране, и мы - одни из тех, кто этот поможет стране его сделать'/>
	  		<Footer lang={lang}/>
		</body>
	  </>
  );
}

export default App;
