import React, {useState} from 'react'
import Main from './Main.js'
import Header from './Header.js'
// import Footer from './Footer.js'

function App() {
	let width = 50;
	let height = 50;



//	const [focus, setFocus] = useState(['asd', 'sdf']);
	let focus = ['', ''];



	function setStartOfScope(e){
		const table = document.querySelectorAll('.cell');
		const ident = document.getElementById("cell-name");
		console.log(table);
//		let nextFocus = [...focus];
//		console.log("1:" + nextFocus);
//		nextFocus[0] = e.target.id;
//		console.log("2:" + nextFocus);
//		console.log([...nextFocus]);
//		setFocus(['', '']);
		focus[0] = e.target.id;
		console.log("3:" + focus);
		ident.value = focus[0];
		table.forEach((cell) => cell.addEventListener("mouseenter", setEndOfScope(e)));
		console.log(e.target.id);
		console.log(focus);
	}
	function setEndOfScope(e){
		const ident = document.getElementById("cell-name");
//		let nextFocus = [...focus];
//		nextFocus[1] = e.target.id;
//		setFocus(f => [...nextFocus]);
		focus[1] = e.target.id;
		ident.value = focus[0] + ":" + focus[1];
		ident.value = focus[0];
		console.log(focus);
		console.log(ident.value);
	}
	function setFinalEndOfScope(){
		let table = document.querySelectorAll(".cell");
//		setMouseDown(false);
		table.forEach((cell) => cell.removeEventListener("mouseenter", setEndOfScope));
		console.log(focus);
//		console.log(mouseDown);
	}
	// выделение ячейки

//	function setFocus(e) {
 //               const focus = document.getElementById('cell-name');              
//		const cellmonitor = document.getElementById('cell-value');
//		cellmonitor.value = e.target.value;
 //               focus.value = e.target.id;
  //      }




	// обновление ячейки

	function cellChange(e){
		const cellmonitor = document.getElementById('cell-value');
		cellmonitor.value= e.target.value;
	}
	function monitorChange(e){
		const cellId = document.getElementById('cell-name').value;
		const cell = document.getElementById(cellId);
		cell.value = e.target.value;
	}

	// жирни шрифт

	function setBold() {
		const focus = document.getElementById('cell-name');
		const cell = document.getElementById(focus.value);
		if(cell.style.fontWeight==='bold'){
			cell.style.fontWeight='normal';
		} else {
			cell.style.fontWeight='bold';
		}

	}




	// кривой шрифт

	function setItalic() {
		const focus = document.getElementById('cell-name');
		const cell = document.getElementById(focus.value);
		if(cell.style.fontStyle==='italic'){
			cell.style.fontStyle='normal';
		} else {
			cell.style.fontStyle='italic';
		}
	}





	//шрифт на полу

	function setUnderlined() {
		const focus = document.getElementById('cell-name');
		const cell = document.getElementById(focus.value);
		if(cell.style.textDecoration==='underline'){
			cell.style.textDecoration='';
		} else{
			cell.style.textDecoration='underline';
		}

	}


//-------------------------------------------------



	return(
		<>
			<head>
				<link rel="preconnect" href="https://fonts.googleapis.com"></link>
				<link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin></link>
				<link href="https://fonts.googleapis.com/css2?family=Open+Sans:ital,wght@0,300..800;1,300..800&display=swap" rel="stylesheet"></link>

			</head>
			<Header setBold={setBold} setItalic={setItalic} setUnderlined={setUnderlined} monitorChange={monitorChange}/>
			<Main finalScope={setFinalEndOfScope} setEnd={setEndOfScope} setStart={setStartOfScope} height={height} width={width} onClick={setFocus} onChangeCell={cellChange}/>
		</>
	)
}
export default App;
